import styles from './menu.module.css'
import React from 'react'
import { Link, useLocation } from 'react-router-dom'

export default function Menu() {

    const localizacao = useLocation()

  return (
    <header>
        <nav className={styles.navegacao}>
            <Link className={`${styles.link} ${localizacao.pathname === '/' ? styles.linkDestacado : ""} ` } to="/" > Ínicio</Link>
            <Link className={`${styles.link} ${localizacao.pathname === '/sobremim' ? styles.linkDestacado : ""} `} to="/sobremim">Sobre mim</Link>
        </nav>
    </header>

  )
}
