import React from 'react'
import { Route, Routes, useParams } from 'react-router-dom'
import posts from 'json/posts.json'
import PostModelo from 'componentes/postModelo'
import ReactMarkdown from "react-markdown"
import './post.css'
import NaoEncontrado from 'paginas/naoEncontrado'
import Padrao from 'componentes/padrao'
import Recomendados from 'componentes/recomendados'

export default function Post() {

    const parametros = useParams()
    const post = posts.find((post) => {
        return post.id == parametros.id

    })
    if (!post) {
        return <NaoEncontrado />
    }

    const postsRecomendados = posts
    .filter((post) => post.id !== Number(parametros.id))
    .sort((a, b)=> b.id - a.id)
    .slice(0,4)
   

    return (

        <Routes>
            <Route path='*' element={<Padrao />}>
                <Route index element={
                    <PostModelo
                        fotoCapa={`/assets/posts/${post.id}/capa.png`}
                        titulo={post.titulo}



                    >
                        <div className="post-markdown-container">
                            <ReactMarkdown>
                                {post.texto}
                            </ReactMarkdown>
                        </div>

                        <h1>Outros Posts que você pode gostar:</h1>
        <ul>
            {postsRecomendados.map((post)=>(<li key={post.id}><Post post={post}/></li>))}
        </ul>



                    </PostModelo>} />
                    

            </Route>
        </Routes>

    )
}
